import React from 'react'
import { TODO_ACTIONS } from './Todoactions'
export const INITIAL_TODO={
  // items:[{
  //   time: Date.now(),
  //   description:'',
  //   isCOmpleted : false,
  // }],
  items: [
    { time:Date.now(), description:'',isCompleted:false},
  ],
}
export const PostTodo =(state,action)=>{
  switch(action.type){
    case TODO_ACTIONS.ADD_TODO:
      console.log(state)
      console.log(action.payload)
      return {
        ...state,
        items: [...state.items, action.payload],
      };
    case TODO_ACTIONS.EDIT_TODO:
      return{}
    case TODO_ACTIONS.REMOVE_TODO:
      return{}
    case TODO_ACTIONS.COMPLETE:
      return{}
    case TODO_ACTIONS.INCOMPLETE:
      return{}
    default:
      return state
  }
}
