import React, { useState } from 'react'
import { TODO_ACTIONS } from './Todoactions.js'
import { INITIAL_TODO } from './PostTodo.jsx'
import { useReducer } from 'react'
import { PostTodo } from './PostTodo.jsx' 
import { TaskCard } from './TaskCard.jsx'
import { v4 as uuidv4 } from 'uuid';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  faTrashCan } from '@fortawesome/free-solid-svg-icons';
import './index.css'
import { removeidentificaotr } from './TaskCard.jsx'



export const dispatcher =(state,action)=>{
  switch(action.type){
    case 'GET_ID':
      globalremoveid=action.payload
      return;
    default:
    return state;
  }
}
const TodoForm = () => {

  const date = new Date();
  const datecal = date.toLocaleDateString()
  const [state,dispatch] = useReducer(PostTodo,INITIAL_TODO)
  const [task,settask] = useState('');
  const [id,setid] = useState('')
  function handelsubmit(e){
    e.preventDefault();
    const idi = uuidv4();
    const newItem = { time:datecal, description:task,isCompleted:false ,id:idi};
    dispatch({type:TODO_ACTIONS.ADD_TODO, payload: newItem})
    settask('');
    console.log(removeidentificaotr)
  }

  const handelclick = (id)=>{
    dispatch({type: TODO_ACTIONS.REMOVE_TODO})
  }

  const clear=()=>{
    dispatch({type: TODO_ACTIONS.CLEAR_TODO})
  }

  return (
    <div className='container'>
      <form 
      onSubmit={(e)=>{
        handelsubmit(e)
      }}
      style={{
        display: 'flex',
        flexDirection:'row'
      }}>

        <textarea 
          value={task}
          onChange={(e) =>{
          settask(e.target.value)
          // console.log(task)
          // console.log(state)
        }} 
          >
        </textarea>
        <button type='submit' className='btn'>ADD TASK</button>
        <button type='reset' onClick={clear} className='btn'>Clear List</button>

      </form> 

      <p style={{
        position:'relative',
        left: '30px',
      }}>odos</p>
      {

      state.items.map(item => 
         (<>
          <TaskCard key={item.id} time={item.time} desc = {item.description} id = {item.id} state={state}>
            </TaskCard> 
            {/* <button type='reset' onClick={() =>handelclick(item.id)}>delete</button>
            <FontAwesomeIcon className='faw'icon= {faTrashCan} /> */}
           {console.log(state.inicatord)}
          </>
         )
      )
      }

  </div>
  )
}
export default TodoForm;
