import React, { useState } from 'react'
import { TODO_ACTIONS } from './Todoactions.js'
import { INITIAL_TODO } from './PostTodo.jsx'
import { useReducer } from 'react'
import { PostTodo } from './PostTodo.jsx' 
import { TaskCard } from './TaskCard.jsx'
import { v4 as uuidv4 } from 'uuid';

const TodoForm = () => {
  const date = new Date();
  const datecal = date.toLocaleDateString()
  const [state,dispatch] = useReducer(PostTodo,INITIAL_TODO)
  const [task,settask] = useState('');
  function handelsubmit(e){
    e.preventDefault();
    const idi = uuidv4();
    const newItem = { time:datecal, description:task,isCompleted:false ,id:idi};
    dispatch({type:TODO_ACTIONS.ADD_TODO, payload: newItem})
    settask('');
  }
  return (
    <div className='container'>
      <form 
      onSubmit={(e)=>{
        handelsubmit(e)
      }}
      style={{
        display: 'flex',
        flexDirection:'row'
      }}>

      <textarea 
        value={task}
        onChange={(e) =>{
        settask(e.target.value)
        // console.log(task)
        // console.log(state)
      }} 
        >

      </textarea>
      <button className='btn'>ADD TASK</button>
    </form>
      <p>Todos</p>
      <div className='containerlist'>
      {
      state.items.map(item => (
         <TaskCard key={item.id} time={item.time} desc = {item.description} id = {item.id}></TaskCard>
        ))
      }
    </div>
  </div>
  )
}

export default TodoForm;
