export const INITIAL_STATE={
    title: '',
    desc:'',
    price:0,
    category:'',
    tags:[],
    quantity: 0,

};
 export const  postreducer=(state , action)=>{
    switch( action.type){
        case 'CHANGE_INPUT':
            return {
                ...state,
                [action.payload.name]:action.payload.value,
            };
       case 'ADD_TAG':
            return {};
        case 'REMOVE_TAG':
            return {};
        case 'INCREASCE':
            return {
                ...state,
                quantity: state.quantity+1,
            };
        case 'DECREASE':
            return {
                ...state,
                quantity: state.quantity-1,
            };
        default :
            return state; 
    }
}
