import React, { useState } from 'react'
import { TODO_ACTIONS } from './Todoactions.js'
import { INITIAL_TODO } from './PostTodo.jsx'
import { useReducer } from 'react'
import { PostTodo } from './PostTodo.jsx' 
import { TaskCard } from './TaskCard.jsx'
import { v4 as uuidv4 } from 'uuid';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faduotone, faTrash, faTrashCan } from '@fortawesome/free-solid-svg-icons';
import './index.css'

const TodoForm = () => {


  const date = new Date();
  const datecal = date.toLocaleDateString()
  const [state,dispatch] = useReducer(PostTodo,INITIAL_TODO)
  const [task,settask] = useState('');



  function handelsubmit(e){
    e.preventDefault();
    const idi = uuidv4();
    const newItem = { time:datecal, description:task,isCompleted:false ,id:idi};
    dispatch({type:TODO_ACTIONS.ADD_TODO, payload: newItem})
    settask('');
  }

  const handelclick = (id)=>{
    dispatch({type: TODO_ACTIONS.REMOVE_TODO,payload:id})
  }

let index = true
  const clear=()=>{

    dispatch({type: TODO_ACTIONS.CLEAR_TODO})
    console.log('arayclear: '+ state.items.length)
    index = false;
  }
  return (
    <div className='container'>
      <form 
      onSubmit={(e)=>{
        handelsubmit(e)
      }}
      style={{
        display: 'flex',
        flexDirection:'row'
      }}>

      <textarea 
        value={task}
        onChange={(e) =>{
        settask(e.target.value)
        // console.log(task)
        // console.log(state)
      }} 
        >

      </textarea>
      <button type='submit' className='btn'>ADD TASK</button>

    </form> <button onClick={clear} className='btn'>Clear List</button>
      <p>Todos</p>
      <div className='containerlist'>
      {
      state.items.map(item => (
         index ? (<div key={item.id}>
          <TaskCard key={item.id} time={item.time} desc = {item.description} id = {item.id}>
            {console.log(item)}
          </TaskCard>
              <FontAwesomeIcon className='faw'icon= {faTrashCan} />
         </div>) :(<div>acdas</div>)
        )
        )
      }
    </div>
  </div>
  )
}

export default TodoForm;
