import { useReducer } from 'react';
import { INITIAL_STATE } from './postreducer';
import { postreducer } from './postreducer';
import './index.css'
import { useRef } from 'react';
const Form = () => {
const [state,disptch] = useReducer(postreducer,INITIAL_STATE)
    const handelchange =e=>{
        disptch({type:"CHANGE_INPUT", payload:{name:e.target.name,value:e.target.value}})
        console.log(state)
    }
    const tagref = useRef();
    const hadeltagclick = () =>{
        const tagZ = tagref.current.value.split(',');
        tagZ.forEach(e =>{
            disptch({type:'ADD_TAG',payload:e})
        })
    }
    return (
        <div>
            <form>
                <input
                type="text"
                placeholder="Title"
                name="title"
                onChange={handelchange}
                />
                <input
                type="text"
                placeholder="Desc"
                name="desc"
                onChange={handelchange}
                />
                <input
                type="number"
                placeholder="Price"
                onChange={handelchange}
                name="price"
                />
                <p>Category:</p>
                <select  name="category">
                <option value="sneakers">Sneakers</option>
                <option value="tshirts">T-shirts</option>
                <option value="jeans">Jeans</option>
                </select>
                <p>Tags:</p>
                <textarea
                ref={tagref}
                placeholder="Seperate tags with commas..."
                ></textarea>
                <button onClick={hadeltagclick} type="button">
                Add Tags
                </button>
                <div className="tags">
                {state.tags.map((e)=>(
                    <small key={e} 
                    //  onClick={disptch({type:'REMOVE_TAG', payload:e})}     
                    >{e}</small>
                ))}
                </div>
                <div className="quantity">
                <button onClick={()=> disptch({type:"DECREASE"})} type="button">
                    -
                </button>
                <span>Quantity({state.quantity})</span>
                <button onClick={()=> disptch({type:"INCREASE"})} type="button">
                    +
                </button>
                </div>
            </form>
            </div>
        );
        };

export default Form;
