import React, { useState } from 'react'
import { TODO_ACTIONS } from './Todoactions.js'
import { INITIAL_TODO } from './PostTodo.jsx'
import { useReducer } from 'react'
import { PostTodo } from './PostTodo.jsx' 
import { TaskCard } from './TaskCard.jsx'

const TodoForm = () => {
  const date = new Date();
  const [state,dispatch] = useReducer(PostTodo,INITIAL_TODO)
  const [task,settask] = useState('');
  function handelsubmit(e){
    e.preventDefault();
    const newItem = { time:date.toLocaleDateString(), description:task,isCompleted:false };
    dispatch({type:TODO_ACTIONS.ADD_TODO, payload: newItem})
    console.log(state)
    settask('');
  }
  return (
    <div>
      <form 
      onSubmit={(e)=>{
        handelsubmit(e)
      }}
      style={{
        display: 'flex',
        flexDirection:'row'
      }}>

      <textarea 
        value={task}
        onChange={(e) =>{
        settask(e.target.value)
        // console.log(task)
        // console.log(state)
      }} 
        >

      </textarea>
      <button >ADD TASK</button>
    </form>
    <div>
      <p>Todos</p>
      <div>
      {
      state.items.map(item => (
         <TaskCard key={item.time} time={item.time} desc = {item.description}></TaskCard>
        ))
      }
      </div>
    </div>
  </div>
  )
}

export default TodoForm;
