export const INITIAL_STATE={
    title: '',
    desc:'',
    price:0,
    category:'',
    tags:[],
    quantity: 0,

};
 export const  postreducer=(state , action)=>{
    switch( action.type){
        case 'CHANGE_INPUT':
            return {
                ...state,
                [action.payload.name]:action.payload.value,
            };
       case 'ADD_TAG':
            return {
                ...state,
                tags : [...state.tags,action.payload]
            };
        case 'REMOVE_TAG':
            return {
                ...state,
                tags : state.tags.filter(e =>e!==action.payload)
            };
        case 'INCREASE':
            return {
                ...state,
                quantity: state.quantity+1,
            };
        case 'DECREASE':
            if(state.quantity === 0){
                return state
            }else{
                return {
                    ...state,
                    quantity: state.quantity-1,
                }
            }
        default :
            return state; 
    }
}
