import React from 'react'
import './index.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faduotone, faTrash, faTrashCan } from '@fortawesome/free-solid-svg-icons';
import { useReducer } from 'react';
import { INITIAL_TODO, PostTodo } from './PostTodo';
import { TODO_ACTIONS } from './Todoactions';


export const TaskCard = (props) => {
  const [state,dispatch] = useReducer(PostTodo,INITIAL_TODO)
  const handelclick = ()=>{
    dispatch({type: TODO_ACTIONS.REMOVE_TODO,payload: props.id})
  }
  return(
    <div className='todo-row'>
        <h4>{props.time}</h4>
        <h5>{props.id}</h5>
            <div>
                <h3>{props.desc}</h3>
            </div>
            <FontAwesomeIcon className='faw'icon= {faTrashCan} />
    </div>
    )
}
