import React from 'react'
import './index.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faTrashCan } from '@fortawesome/free-solid-svg-icons';
import {FaSquareCheck} from '@fortawesome/free-solid-svg-icons'


export const TaskCard = (props) => {

  const handelclick = ()=>{
    props.remove(props.id)
  }
  const handelcomplete = ()=>{
    props.complete(props.id)
  }


  return(
    <div 
    //className='todo-row'
     className={!props.completeproperty?'todo-row':'todo-row-comp'}
      >
        <h4 className={props.completeproperty?'compl':''}>{props.time}</h4>
            <div >
              <h4>{props.id}</h4>
                <h3 className={props.completeproperty?'compl':''}>{props.desc}</h3>
            </div>
            <a href='#/ds' className='faw'onClick={handelclick} >
              <FontAwesomeIcon  icon={faTrashCan}/> 
            </a>
            <a>

            </a>
            <input type={'checkbox'} className="chck" onChange={handelcomplete}/>


    </div>
    )
}
